
import React, { useState, useEffect } from 'react';
import { AnimatedBackgroundProps } from '../types/blinkie';

const backgroundStyles = {
  geometricPatterns: [
    "bg-[radial-gradient(#e81cff_15%,transparent_16%),_radial-gradient(#7a19ff_15%,transparent_16%)]",
    "bg-[radial-gradient(#ff8800_15%,transparent_16%),_radial-gradient(#ff3300_15%,transparent_16%)]",
    "bg-[radial-gradient(#00ddff_15%,transparent_16%),_radial-gradient(#00aaff_15%,transparent_16%)]",
    "bg-[radial-gradient(#4caf50_15%,transparent_16%),_radial-gradient(#8bc34a_15%,transparent_16%)]",
  ],
  stars: [
    "bg-stars bg-repeat opacity-40 bg-blend-screen",
    "bg-stars bg-repeat opacity-30 bg-[#000720] bg-blend-screen",
    "bg-stars bg-repeat opacity-50 bg-[#100030] bg-blend-screen",
    "bg-stars bg-repeat opacity-40 bg-[#002010] bg-blend-screen",
  ],
  oldWeb: [
    "bg-[url('data:image/svg+xml;base64,')]",
    "bg-[url('data:image/svg+xml;base64,')]",
    "bg-[url('data:image/svg+xml;base64,')]",
    "bg-[url('data:image/svg+xml;base64,')]",
  ],
};

const AnimatedBackground: React.FC<AnimatedBackgroundProps> = ({ 
  backgroundType = 'oldWeb', 
  speed = 15000 
}) => {
  const [currentBgIndex, setCurrentBgIndex] = useState(0);
  const backgroundOptions = backgroundType === 'geometricPatterns' 
    ? backgroundStyles.geometricPatterns 
    : backgroundType === 'stars' 
      ? backgroundStyles.stars 
      : backgroundStyles.oldWeb;

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentBgIndex(prevIndex => (prevIndex + 1) % backgroundOptions.length);
    }, speed);
    
    return () => clearInterval(interval);
  }, [backgroundOptions.length, speed]);

  return (
    <div className={`fixed inset-0 w-full h-full z-[-1] transition-all duration-3000 ${backgroundOptions[currentBgIndex]}`} />
  );
};

export default AnimatedBackground;
